import { useState } from 'react';
import '../css/App.css';
import AddModalWin from './addModalWin';
import ToDoList from './toDoList'

const posts = [
  {id: 1, title: 'Привет, мир'},
  {id: 2, title: 'Установка'}
];

function App() {
  const [modalActive, setActive] = useState(true);

  const date = new Date();
  const fullDate = new Intl.DateTimeFormat("ru", {dateStyle: "full"}).format(date);
  return (
    <div className="App">
      <h1>{fullDate}</h1>
      <AddModalWin activ={ModalActive} setActive={setActive}/>
      <ToDoList posts={posts} />
    </div>
  );
}

export default App;
