import { useState } from 'react';
import '../css/App.css';
import AddModalWin from './addModalWin';
import ToDoList from './toDoList'

const posts = [
  {id: 1, title: 'Привет, мир'},
  {id: 2, title: 'Установка'}
];

function App() {
  const [modalActive, setModalActive] = useState(false);

  const date = new Date();
  const fullDate = new Intl.DateTimeFormat("ru", {dateStyle: "full"}).format(date);
  return (
    <div className="App">
      <h1>{fullDate}</h1>
      <button className='button' onClick={() => setModalActive(true)}>Добавить задачу</button>
      <AddModalWin activ={modalActive} setActive={setModalActive}>
        <h2>Добавить задачу на день</h2>
        <label htmlFor="description">Описание*</label><br />
          <textarea {...register("description", {required:  'Введите описание проекта'})}
          id="description" className="comment" cols="40" rows="3"></textarea><br />
          {
              errors?.description && (
                  <div style={{color: 'red'}}>{errors.description.message}</div>
              )
          }

          <input id="buttonOrderForm" type="submit" value="Заказать"></input>
      </AddModalWin>
      <ToDoList posts={posts} />
    </div>
  );
}

export default App;
