import React from 'react';
import posts from './posts'



const ToDoList = (props) =>{
    const delToDO = () =>{
        props.setToDoList( posts.filter(a =>
            a.id !== post.id
          ))
        console.log(posts)
    }
    const sidebar = (
        <ul>
            {props.posts.length ? 
            props.posts.map((post) =>
            <li key={post.id}>
                <input  type='checkbox' id={post.id}/>
                <label htmlFor={post.id}> {post.title}</label>
                <input id='inDel' type='button' value='Удалить' onClick={() => delToDO()}/>
            </li>
            ): (<p>Нет задач</p>)}
        </ul>
    );
    return (
        <div className='list'>
            {sidebar}
        </div>
    )
}

export default ToDoList;
