import { useState } from 'react';
import '../css/App.css';
import AddModalWin from './addModalWin';
import ToDoList from './toDoList'

const posts = [
  {id: 1, title: 'Привет, мир'},
  {id: 2, title: 'Установка'}
];

function App() {
  const [modalActive, setModalActive] = useState(false);

  const date = new Date();
  const fullDate = new Intl.DateTimeFormat("ru", {dateStyle: "full"}).format(date);
  return (
    <div className="App">
      <h1>{fullDate}</h1>
      <button className='button' onClick={() => setModalActive(true)}>Добавить задачу</button>
      <AddModalWin activ={modalActive} setActive={setModalActive}>
        <button style={{ textAlign: 'rigth'}} className='buEx' onClick={() => setModalActive(false)}>❌</button>
        <h2>Добавить задачу на день</h2>
        <input id="inToDo" type="text" /><br />
        <button className='button' >Добавить задачу</button>
      </AddModalWin>
      <ToDoList posts={posts} />
    </div>
  );
}

export default App;
